mapper.select:
select

from


	a.del_flag = 0

		and a.equ_id = #{equId}


		and a.defect_type_id = #{defectTypeId}


		and a.defect_describe = #{defectDescribe}


		and a.data_variable = #{dataVariable}


		and a.data_variable = #{dataVariable}


		and a.defect_data_level = #{defectDataLevel}


		and a.speed_type = #{speedType}


		and a.system_id = #{systemId}


		and a.defect_data_category = #{defectDataCategory}


		and a.min_speed = #{minSpeed}


		and a.max_speed = #{maxSpeed}


		and a.expression = #{expression}

select

from


	  a.del_flag=0

     and a.equ_id = #{equId}


     and a.defect_type_id = #{defectTypeId}


     and a.defect_describe = #{defectDescribe}


     and a.data_variable = #{dataVariable}


     and a.defect_data_level = #{defectDataLevel}


     and a.speed_type = #{speedType}


     and a.system_id = #{systemId}


     and a.defect_data_category = #{defectDataCategory}


     and a.sort = #{sort}


order by a.system_id,a.sort

mapper.insert:
insert into 6c_defect_assortment (id, equ_id, defect_type_id, 
  defect_describe, data_variable, defect_data_level,speed_type,
  system_id, sort, del_flag,defect_data_category,min_speed,max_speed,expression, 
  create_ip, create_by, create_date
  )
values (#{id}, #{equId}, #{defectTypeId}, 
  #{defectDescribe}, #{dataVariable}, #{defectDataLevel}, #{speedType},
  #{systemId}, #{sort}, #{delFlag},#{defectDataCategory},#{minSpeed},#{maxSpeed},#{expression},
  #{createIp}, #{createBy}, #{createDate}
  )

mapper.update:
update 6c_defect_assortment
set del_flag=1,delete_ip = #{deleteIp},delete_by=#{deleteBy},delete_date=#{deleteDate}
  where id in 

  #{item}
