mapper.select:
select

from
6c_defect_history_delay a


		and a.delay_person = #{delayPerson}


		and a.delay_date = #{delayDate}


		and a.delay_to_date = #{delayToDate}


		and a.remark = #{remark}


		and a.defect_id = #{defectId}


		and a.delay_status = #{delayStatus}

mapper.insert:
insert into 6c_defect_history_delay(
id, delay_person, delay_date, delay_to_date, remark, defect_id,
delay_status
,sort
)
values(
#{id}, #{delayPerson}, #{delayDate}, #{delayToDate}, #{remark}, #{defectId}, 
#{delayStatus}
,#{sort}
)
