mapper.select:
select

from
6c_defect_reform_handle a


		and a.handle_person = #{handlePerson}


		and a.handle_date = #{handleDate}


		and a.handle_scheme = #{handle_scheme}


		and a.handle_value = #{handleValue}   


		and a.informant = #{informant}


		and a.handle_before_photo = #{handleBeforePhoto}


		and a.handle_status = #{handleStatus}

mapper.insert:
insert into 6c_defect_reform_handle(
id, handle_person, handle_date, handle_scheme, handle_photo, handle_value,
informant, handle_before_photo, pillar_photo, remark, handle_status
)
values(
#{id}, #{handlePerson}, #{handleDate}, #{handleScheme}, #{handlePhoto}, #{handleValue}, 
#{informant}, #{handleBeforePhoto}, #{pillarPhoto}, #{remark}, #{handleStatus}
)
