mapper.select:
select

from
6c_expire_task a
where
a.data_id = #{dataId}
and a.bussiness_type = #{bussinessType}

select

from
6c_expire_task a


		and a.create_date = #{createDate}


		and a.expire_date = #{expireDate}


		and a.bussiness_type = #{bussinessType}


		and a.payload = #{payload}


		and a.lock_version = #{lockVersion}


		and a.data_id = #{dataId}

mapper.insert:
insert into 6c_expire_task(
id, create_date, expire_date, bussiness_type, payload,
lock_version, data_id
)
values(
#{id}, #{createDate}, #{expireDate}, #{bussinessType}, #{payload},
#{lockVersion}, #{dataId}
)

mapper.update:
update 6c_expire_task
set expire_date = #{expireDate}
where  data_id = #{dataId} and bussiness_type = #{bussinessType}
