SELECT COUNT(DISTINCT a.id) AS pENum, COUNT(CASE WHEN a.execute_status='5' THEN a.id END) AS reformPENum, COUNT(CASE WHEN (d.defect_data_status='1' and d.del_flag = 0) OR (e.defect_data_status='1' and e.del_flag = 0) OR (f.defect_data_status='1' and f.del_flag = 0) THEN a.id END) AS analysisDataNum, COUNT(CASE WHEN (d.plan_id=pd.id and d.del_flag = 0) OR (e.plan_id=pd.id and e.del_flag = 0) OR (f.plan_id=pd.id and f.del_flag = 0) THEN pd.id END) AS uploadDataNum
FROM 6c_plan_execute a LEFT JOIN 6c_plan_base pb ON a.plan_base_id = pb.id LEFT JOIN 6c_plan_execute_record per ON a.id = per.plan_execute_id LEFT JOIN 6c_plan_detail pd ON pd.plan_base_id = pb.id LEFT JOIN 6c_c1_info d ON d.plan_id = pd.id LEFT JOIN 6c_c2_info e ON e.plan_id = pd.id LEFT JOIN 6c_c4_info f ON f.plan_id = pd.id
a.del_flag = 0
and pb.plan_date >= #{beginCheckDate}
and pb.plan_date <= #{endCheckDate}
and pd.line_id = #{lineId}
and pd.direction = #{direction}
and pd.start_station =#{startStation}
and pd.end_station =#{endStation}
and((a.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.dept_id = #{did}) or (per.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or per.dept_id = #{did}) or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.responsible_dept_id = #{did}) )
and (per.dept_id = #{did} or a.responsible_dept_id=#{did} or a.dept_id=#{did} )
and (a.section_id = #{sectionId} or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${sectionId}%' and del_flag=0) or a.responsible_dept_id = #{sectionId}))

select count(a.id) AS num,DATE_FORMAT(pb.plan_date,'%m') AS planDate ,a.system_id AS systemId,COUNT(CASE WHEN a.execute_status='5' THEN a.id END) AS cancelNum from 6c_plan_execute a left join 6c_plan_base pb on a.plan_base_id = pb.id left join 6c_plan_detail pd on pd.plan_base_id = pb.id left join 6c_plan_execute_record per on a.id = per.plan_execute_id
a.del_flag = 0
and((a.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.dept_id = #{did}) or (per.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or per.dept_id = #{did}) or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.responsible_dept_id = #{did}) )
and (per.dept_id = #{did} or a.responsible_dept_id=#{did} or a.dept_id=#{did} )
and (a.section_id = #{sectionId} or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${sectionId}%' and del_flag=0) or a.responsible_dept_id = #{sectionId}))
and pb.plan_date >= #{beginCheckDate}
and a.execute_status = #{executeStatus}
and pb.plan_date <= #{endCheckDate}
and a.system_id = #{systemId}
group by planDate,systemId

select count(a.id) as num,p.name AS lineName,pd.line_id as lineId ,count(case when a.execute_status='5' THEN a.id END) AS cancelNum ,a.system_id AS systemId from 6c_plan_execute a left join 6c_plan_base pb ON a.plan_base_id = pb.id left join 6c_plan_detail pd on pd.plan_base_id = pb.id left join p_line p on p.id = pd.line_id left join 6c_plan_execute_record per on a.id = per.plan_execute_id
a.del_flag = 0
and a.system_id = #{systemId}
and((a.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.dept_id = #{did}) or (per.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or per.dept_id = #{did}) or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.responsible_dept_id = #{did}) )
and (per.dept_id = #{did} or a.responsible_dept_id=#{did} or a.dept_id=#{did} )
and (a.section_id = #{sectionId} or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${sectionId}%' and del_flag=0) or a.responsible_dept_id = #{sectionId}))
and pb.plan_date >= #{beginCheckDate}
and a.execute_status = #{executeStatus}
and pb.plan_date <= #{endCheckDate}
group by pd.line_id,a.system_id

select count(a.id) as num,a.execute_status as status from
a.del_flag = 0
and((a.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.dept_id = #{did}) or (per.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or per.dept_id = #{did}) or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.responsible_dept_id = #{did}) )
and (per.dept_id = #{did} or a.responsible_dept_id=#{did} or a.dept_id=#{did} )
and (a.section_id = #{sectionId} or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${sectionId}%' and del_flag=0) or a.responsible_dept_id = #{sectionId}))
and pb.plan_date >= #{beginCheckDate}
and pb.plan_date <= #{endCheckDate}
and a.system_id = #{systemId}
and a.flow_tag = #{flowTag}
group by a.execute_status

select round(count(case when a.execute_status in (5) theN a.id end)/count(a.id)*100,2) as nub ,line.name as name ,a.system_id as name from 6c_plan_execute a left join 6c_plan_base pb on a.plan_base_id = pb.id left join 6c_plan_detail pd on pd.plan_base_id = pb.id left join 6c_plan_execute_record per on a.id = per.plan_execute_id left join p_line line on pd.line_id = line.id
a.del_flag = 0
and((a.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.dept_id = #{did}) or (per.dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or per.dept_id = #{did}) or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${did}%' and del_flag=0) or a.responsible_dept_id = #{did}) )
and (per.dept_id = #{did} or a.responsible_dept_id=#{did} or a.dept_id=#{did} )
and (a.section_id = #{sectionId} or (a.responsible_dept_id in (select id from p_department where parent_ids like '%${sectionId}%' and del_flag=0) or a.responsible_dept_id = #{sectionId}))
and pb.plan_date >= #{beginCheckDate}
and a.execute_status = #{executeStatus}
and pb.plan_date <= #{endCheckDate}
group by a.system_id group by pd.line_id order by nub desc limit 1

