mapper.sql:

	and a.name = #{name}


	and a.code = #{code}


	and a.type = #{type}


	and a.assortment_id = #{assortmentId}


	and a.version = #{version}


	and a.upload_date = #{uploadDate}


	and a.file_id = #{fileId}


	and a.remark = #{remark}


	and a.field1 = #{field1}


	and a.field2 = #{field2}


	and a.field3 = #{field3}


	and a.field4 = #{field4}


	and a.field5 = #{field5}


	and a.field6 = #{field6}


	and a.field7 = #{field7}


	and a.field8 = #{field8}


	and a.field9 = #{field9}


	and a.field10 = #{field10}


	and a.sort = #{sort}


	and a.pavilion_id = #{pavilionId}


	and a.psa_id = #{psaId}

and a.del_flag=0

mapper.select:
select

from


	a.del_flag=0

		and a.type=#{type}


		and a.code=#{code}


		and a.pavilion_id=#{pavilionId}


		and a.psa_id=#{psaId}


		and (a.assortment_id in
		(select id from sys_assortment
		where parent_ids like '%${assortmentId}%')
		or
		a.assortment_id=#{assortmentId})


		and p.name like '%${assortmentName}%'


		and a.name like '%${name}%'


		and a.file_id = #{fileId}


		and f.real_name like '%${fileName}%'


		and a.version = #{version}


		and a.upload_date = #{uploadDate}


		and a.remark = #{remark}


order by a.upload_date desc
