mapper.select:
		select 
		from 

		 	bcp.del_flag = 0



					and (
							bcp.line_id in 
							(
								select distinct pl_id from p_dept_line where dept_id in
								(select id from p_department where parent_ids like '%${deptId}%')  or dept_id=#{deptId} 
							)
						)


					and bcp.line_id in (select distinct pl_id from p_dept_line where dept_id=#{deptId})



					and bcp.line_id in (select distinct pl_id from p_dept_line where section_id=#{sectionId})




                order by bcp.${orderBy}


               order by convert(pl.name using gbk) asc, bcp.sort desc



			limit #{page.startNum},#{page.endNum}

mapper.update:
update bd_construct_position
line_id = #{lineId}, file_id = #{fileId}, sort = #{sort}, update_ip = #{updateIp}, update_by = #{updateBy}, update_date = #{updateDate},
where id = #{id}

update bd_construct_position
set del_flag=#{delFlag},delete_by=#{deleteBy},delete_date=#{deleteDate},delete_ip=#{deleteIp}
where 
id in 

			#{item}
