mapper.select:
		select 
		from 

		 	bcs.del_flag = 0



					and (
							bcs.line_id in 
							(
								select distinct pl_id from p_dept_line where dept_id in
								(select id from p_department where parent_ids like '%${deptId}%' or id=#{deptId} )  
							)
							and bcs.pavilion_id in 
							(
								select plp.pavilion_id from p_line_pavilion plp left join p_pavilion pp on plp.pavilion_id = pp.id
								 where plp.line_id in 
								(
									select distinct pl_id from p_dept_line where dept_id in
									(select id from p_department where parent_ids like '%${deptId}%' or id=#{deptId})  
								) and pp.work_area_id in 
								(
									(select id from p_department where parent_ids like '%${deptId}%' or id=#{deptId})  
								)
							)
						)


					and (
							bcs.line_id in 
							(
								select distinct pl_id from p_dept_line where dept_id=#{deptId} 
							)
							and bcs.pavilion_id in 
							(
								select plp.pavilion_id from p_line_pavilion plp left join p_pavilion pp on plp.pavilion_id = pp.id
								 where plp.line_id in 
								(
									select distinct pl_id from p_dept_line where dept_id=#{deptId}
								) and pp.work_area_id in 
								(
									select id from p_department where dept_id=#{deptId}
								)
							)and bcs.ps_pd_id in 
							(
								select id from p_supply_power_section pspd where pspd.line_id in
								(
									select distinct pl_id from p_dept_line where dept_id in
									(select id from p_department where dept_id=#{deptId}) 
								) and pspd.dept_id in 
								(
									select id from p_department where dept_id=#{deptId}
								)and pspd.pavilion_id in 
								(
									select plp.pavilion_id from p_line_pavilion plp left join p_pavilion pp on plp.pavilion_id = pp.id
									 where plp.line_id in 
									(
										select distinct pl_id from p_dept_line where dept_id in
										(select id from p_department where dept_id=#{deptId})
									) and pp.work_area_id in 
									(
										select id from p_department where dept_id=#{deptId}
									)
								)
							)
						)



				and (
						bcs.line_id in 
						(
							select distinct pl_id from p_dept_line where section_id=#{sectionId} 
						)
						and bcs.pavilion_id in 
						(
							select plp.pavilion_id from p_line_pavilion plp left join p_pavilion pp on plp.pavilion_id = pp.id
							 where plp.line_id in 
							(
								select distinct pl_id from p_dept_line where section_id=#{sectionId} 
							) and pp.work_area_id in 
							(
								select id from p_department where section_id=#{sectionId} 
							)
						)and bcs.ps_pd_id in 
						(
							select id from p_supply_power_section pspd where pspd.line_id in
							(
								select distinct pl_id from p_dept_line where section_id=#{sectionId}  
							) and pspd.dept_id in 
							(
								select id from p_department where section_id=#{sectionId}
							)and pspd.pavilion_id in 
							(
								select plp.pavilion_id from p_line_pavilion plp left join p_pavilion pp on plp.pavilion_id = pp.id
								 where plp.line_id in 
								(
									select distinct pl_id from p_dept_line where section_id=#{sectionId} 
								) and pp.work_area_id in 
								(
									select id from p_department where section_id=#{sectionId}
								)
							)
						)
					)




                order by bcs.${orderBy}


               order by convert(pl.name using gbk) asc,convert(pp.name using gbk) asc,convert(ps.name using gbk) asc



			limit #{page.startNum},#{page.endNum}

mapper.update:
update bd_corres_stand
line_id = #{lineId}, pavilion_id = #{pavilionId}, ps_pd_id = #{psPdId}, breaker_number = #{breakerNumber}, feeder_number = #{feederNumber}, current_ratio = #{currentRatio}, voltage_ratio = #{voltageRatio}, arm_length = #{armLength}, line_length = #{lineLength}, network_length = #{networkLength}, unit_power_line = #{unitPowerLine}, unit_network_line = #{unitNetworkLine}, total_reactance1 = #{totalReactance1}, total_reactance2 = #{totalReactance2}, total_reactance3 = #{totalReactance3}, sort = #{sort}, update_ip = #{updateIp}, update_by = #{updateBy}, update_date = #{updateDate},
where id = #{id}

update bd_corres_stand
set del_flag=#{delFlag},delete_by=#{deleteBy},delete_date=#{deleteDate},delete_ip=#{deleteIp}
where 
id in 

			#{item}
