mapper.select:
		select 
		from 

		 	btvm.del_flag = 0



					and (
							btvm.line_id in 
							(
								select distinct pl_id from p_dept_line where dept_id in
								(select id from p_department where parent_ids like '%${deptId}%')  or dept_id=#{deptId} 
							)
						)


					and btvm.line_id in (select distinct pl_id from p_dept_line where dept_id=#{deptId})



				and btvm.line_id in (select distinct pl_id from p_dept_line where section_id=#{sectionId})




                order by btvm.${orderBy}


            	order by convert(pl.name using gbk) asc, REPLACE(REPLACE(REPLACE(ifnull(btvm.glb, 0),'K', ''),'k',''),'+','.')+0 asc, id asc



			limit #{page.startNum},#{page.endNum}

( select ,'false' as flag from ( select from where btvm.del_flag = 0 and btvm.id != #{id}
and btvm.line_id = #{lineId}
and REPLACE(REPLACE(REPLACE(ifnull(btvm.glb, 0),'K', ''),'k',''),'+','.')+0 <= (select glb+0 from where btvm.del_flag = 0 and btvm.id=#{id}) order by REPLACE(REPLACE(REPLACE(ifnull(btvm.glb, 0),'K', ''),'k',''),'+','.')+0 desc, btvm.id asc
and 1 = 0
limit 0, 25 ) btvm
order by REPLACE(REPLACE(REPLACE(ifnull(btvm.glb, 0),'K', ''),'k',''),'+','.')+0 asc, btvm.id asc
and 1 = 0
limit 0, 25 ) union all (select ,'true' as flag from where btvm.del_flag = 0 and btvm.id=#{id}) union all ( select ,'false' as flag from where btvm.del_flag = 0 and btvm.id != #{id}
and btvm.line_id = #{lineId}
and REPLACE(REPLACE(REPLACE(ifnull(btvm.glb, 0),'K', ''),'k',''),'+','.')+0 > (select btvm.glb+0 from where btvm.del_flag = 0 and btvm.id=#{id}) order by REPLACE(REPLACE(REPLACE(ifnull(btvm.glb, 0),'K', ''),'k',''),'+','.')+0 asc, btvm.id asc
and 1 = 0
limit 0, 25 )

mapper.update:
update bd_trip_video_menu
line_id = #{lineId}, kind = #{kind}, glb = #{glb}, distance = #{distance}, mark = #{mark}, psa_id = #{psaId}, psa_name = #{psaName}, sort = #{sort}, update_ip = #{updateIp}, update_by = #{updateBy}, update_date = #{updateDate},
where id = #{id}

update bd_trip_video_menu
set del_flag=#{delFlag},delete_by=#{deleteBy},delete_date=#{deleteDate},delete_ip=#{deleteIp}
where 
id in 

			#{item}
